import React from "react";
import { FcLike, FcLikePlaceholder } from "react-icons/fc";
import { toast } from "react-toastify";

const Card = (props) => {
  let course = props.course;
  let likedCourses = props.likedCourses;
  let setLikedCourses = props.setLikedCourses;

  function clickHandler() {
    if (likedCourses.includes(course.id)) {
      // agr pehle se like hua pada
      setLikedCourses((prev) => prev.filter((cid)=>(cid !== course.id)));
      toast.warning("like removed");
    }
    else{
        // pehle se like nahi hai yeh course
        //insert karna h ye course liked courses me 
        if(likedCourses.length===0){
            setLikedCourses([course.id]);
        }
        else{
            // non-empty pehle se
            setLikedCourses((prev) => [...prev, course.id]);
        }
        toast.success("Liked Successfully");
    }
  }

  return (
    <div className="card">
      <div className="img">
        <img src={course.image.url}></img>

        <div className="like">
          <button onClick={clickHandler}>
            {
                !likedCourses.includes(course.id) ?
                 (<FcLikePlaceholder fontSize="1.75rem"/>) :  // placeholder icon
                 (<FcLike fontSize="1.75rem"/>) // like icon
            }
           
          </button>
        </div>
      </div>
      <div className="content">
        <p>{course.title}</p>
        <p>{course.description.lenght>100?
        (course.description.substr(0,100) + "..."):
    (course.description)}</p>
      </div>
    </div>
  );
};

export default Card;
