import React from "react";
import { useParams } from "react-router-dom";
import { toast } from "react-toastify";

const CourseDetail = ({ courses }) => {
  const { id } = useParams(); // get the id from URL

  if (!courses) {
    return <p>Loading...</p>;
  }

  // find the course with this id
  let selectedCourse = null;
  Object.values(courses).forEach(array => {
    array.forEach(course => {
      if (course.id === id) {
        selectedCourse = course;
      }
    });
  });

  if (!selectedCourse) {
    return <p>Course not found.</p>;
  }

  const handleAddToCart = () => {
    toast.success(`${selectedCourse.title} added to cart!`);
    // you can add actual cart logic later
  };

  return (
    <div className="course-detail">
      <img src={selectedCourse.image.url} alt={selectedCourse.title} className="detail-img" />
      <h2>{selectedCourse.title}</h2>
      <p>{selectedCourse.description}+{""}</p>
      {selectedCourse.author && <p><strong>Author:</strong> {selectedCourse.author}</p>}
      {selectedCourse.duration && <p><strong>Duration:</strong> {selectedCourse.duration}</p>}
      <button className="filterbutton" onClick={handleAddToCart}>
        Add to Cart
      </button>
    </div>
  );
};

export default CourseDetail;
