import React, { useContext } from "react";
import { CartContext } from "../contexts/CartContext";
import { Link } from "react-router-dom"; 

const CartPage = () => {
    const { cartItems, removeFromCart } = useContext(CartContext); // get cart data and remove function

    return (
        <div className="cart-page">
            <h2>ðŸ›’ Your Cart</h2>
            {/* If no courses added */}
            {cartItems.length === 0 ? (
                <p>No courses added yet.</p>
            ) : (
                <div className="cart-items">
    {cartItems.map(course => (
        <div key={course.id} className="cart-item-wrapper">
            {/* Wrapping the card inside Link to navigate on click */}
            <Link to={`/course/${course.id}`} className="cart-item-link">
                <div className="cart-item">
                    <img src={course.image.url} alt={course.title} />
                    <div>
                        <h3>{course.title}</h3>
                        <p>{course.description.substring(0, 80)}...</p>
                    </div>
                </div>
            </Link>
            {/* Remove button outside link to prevent accidental navigation */}
                                {/* Button to remove from cart */}
                                <button onClick={() => removeFromCart(course.id)}>Remove</button>
                            </div>
                      
                    ))}
                </div>
            )}
        </div>
    );
};

export default CartPage;
