import { use, useEffect, useState } from "react";
import "./App.css";
import Navbar from "./components/Navbar";
import { apiUrl, filterData } from "./data";
import Filter from "./components/Filter";
import Cards from "./components/Cards";
import { toast } from "react-toastify";
import Spinner from "./components/Spinner";
import CourseDetail from "./components/CourseDetail";
import { Routes, Route } from "react-router-dom";

const App = () => {
  const [courses, setCourses]=useState(null);
  const [loading, setLoading] = useState(true);
  const[category, setCategory] = useState(filterData[0].title); // all value(initial value)



    async function fetchData()  {
      setLoading(true);
      try {
        let res = await fetch(apiUrl);
        let output = await res.json(); // coonverting api data into json format
        // save data into a variable
        setCourses(output.data);
      } catch (error) {
        toast.error("Something went wrong");
      }
      setLoading(false);
    };
    useEffect(()=>{
    fetchData();
  },[]);


 return (
    <div className="maxdiv">
      <Navbar />
      <Routes> {/* ADD ROUTES */}
        <Route
          path="/"
          element={
            <>
              <Filter filterData={filterData} />
        {
          loading?(<Spinner/>):(<Cards courses = {courses} category={category}/>) //sending props
        }
 </>
          }
        />
        <Route path="/course/:id" element={<CourseDetail courses={courses} />} />
      </Routes>
    </div>
  );
};

export default App;
